<script>
export default {
  methods: {
    scrollToElement(id) {
      const element = document.getElementById(id);
      if (element) {
        // Obtener la posición del elemento
        const elementTop = element.getBoundingClientRect().top + window.pageYOffset;

        window.scrollTo({
          top: elementTop - 80,
          behavior: 'smooth'
        });
      }
    },
    scrollToElementTop() {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        })
    }
  }
}
</script>